import React, { useState } from 'react';
import { Form ,Col, Container, FormGroup,} from 'reactstrap';

function Admin() {

  const showAddTourDetails=({}) => {

  const [showAddTourDetails, setShowAddTourDetails] = useState(true);
  

  function handleAddTourButtonClick() {
    setShowAddTourDetails(true);
  }

  function handleDeleteTourButtonClick() {
    setShowAddTourDetails(false);
  }
  
  

  return (
    <Container>
        <Col>
    <div>
      <h1>Tour Details Page</h1>
      {showAddTourDetails ? <AddTourDetailsForm /> : <DeleteTourDetailsButton />}
      <div>
        <button onClick={handleAddTourButtonClick}>Add Tour Details</button>
        <button onClick={handleDeleteTourButtonClick}>Delete Tour Details</button>
      </div>
    </div>
    </Col>
    </Container>
  );
}

function AddTourDetailsForm() {
  // Render the form to add tour details
  const handleChange = e => {
    setShowAddTourDetails(prev=>({...prev, [e.target.id]:e.target.value}))
};

const handleClick = async e=>{
  e.preventDefault();
}
  return (
    <div>
      <h2>Add Tour Details</h2>
      {/* Add form fields and submit button */}
      <Form>
        <FormGroup>
          <input 
            type='text'
            placeholder='Title'
            required
            id='title'
            onChange={handleChange}
          />
        </FormGroup>
        <FormGroup>
          <input
            type='text'
            placeholder='City'
            required
            id='city'
            onChange={handleChange}
          />
        </FormGroup>
        <FormGroup>
          <input
            type='text'
            placeholder='Address'
            required
            id='address'
            onChange={handleChange}
          />
        </FormGroup>
        <FormGroup>
          <input
            type='number'
            placeholder='Distance'
            required
            id='distance'
            onChange={handleChange}
          />
        </FormGroup>
        <FormGroup>
          <input
            type='number'
            placeholder='Price'
            required
            id='price'
            onChange={handleChange}
          />
        </FormGroup>
        <FormGroup>
          <input
            type='number'
            placeholder='MaxGroupSize'
            required
            id='size'
            onChange={handleChange}
          />
        </FormGroup>
        <FormGroup>
          <input
            type='text'
            placeholder='Address'
            required
            id='address'
            onChange={handleChange}
          />
        </FormGroup>
        <FormGroup>
          <input
            type='text'
            placeholder='Description'
            required
            id='desc'
            onChange={handleChange}
          />
        </FormGroup>
        <FormGroup>
          <input
            type='file'
            placeholder='image'
            required
            id='img'
            onChange={handleChange}
          />
        </FormGroup>
      </Form> 
      
    </div>
  );
}

function DeleteTourDetailsButton() {
  // Render the button to delete tour details
  return (
    <div>
      <h2>Delete Tour Details</h2>
      <button>Delete Tour</button>
    </div>
  );
}

}

export default Admin;
