import React,{ useState, useContext } from "react";
import './booking.css';
import { Form, FormGroup, ListGroup, ListGroupItem, Button } from "reactstrap";

import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import { BASE_URL } from "../../utils/config";

const axios = require('axios');

const Booking = ({ tour, avgRating }) => {

    const {price, reviews, title} = tour;
    const navigate = useNavigate();

    const {user} = useContext(AuthContext);

    const [booking, setBooking] = useState({
        userId: user && user._id,
        userEmail: user && user.email,
        tourName: title,
        fullName:'',
        phone:'',
        guestSize: 1,
        bookAt:'',
    });

    const handleChange = e => {
        setBooking(prev=>({...prev, [e.target.id]:e.target.value}))
    };
    
    const serviceFee = 10
    const totalAmount = Number(price) * Number(booking.guestSize) + Number(serviceFee)
    
    //send data to the server
    const handleClick = async e=>{
        e.preventDefault();

        console.log(booking);

      /*  try {
            if(!user || user===undefined || user===null){
                return alert('Plaese sign in')
            }

            const res =  await fetch(`${BASE_URL}/booking`,{
                method:'post',
                headers:{
                    'content-type':'application/json'
            },
                credentials:'include',
                body:JSON.stringify(booking),
            }); 
            
            const result = await res.json();

            if(!res.ok){
                return alert(result.message);
            }
            navigate("/thank-you");
            
        } catch (err) {
            alert(err.message);
        } */

    };

    /*payment gateway starts

    const loadScript = (src) => {
        return new Promise((resolve) => {
          const script = document.createElement('script')
          script.src = src

          script.onload = () => {
             resolve(true)
          }

          script.onerror = () => {
              resolve(false)
          }
          document.body.appendChild(script)
        })
      }

    const displayRazorpay = async (amount) => {
        const res = await loadScript('https://checkout.razorpay.com/v1/checkout.js')
     }
     //payment gateway ends  */

     const handlePayment = (amount) => {
        const _data = { amount: amount };
        console.log('Destination:', 'http://localhost:3000/orders');
         return false;

        fetch('http://localhost:3000/orders',
        
         {
          method: 'POST',
          body: JSON.stringify(_data),
          headers: {
            'Content-Type': 'application/json'
          }
        })
          .then(response => response.json())
          .then(data => {
            console.log(data, "29");
          })
          .catch(error => {
            console.log(error);
          });
      }
      

    return <div className="booking">
        <div className="booking__top d-flex align-items-center
         justify-content-between">
            <h3>₹{price} <span>/per person</span></h3>
            <span className="tour__rating d-flex align-items-center gap-1">
                <i 
                  class="ri-star-fill" 
                  ></i> 
                  {avgRating === 0 ? null : avgRating} ({reviews?.length})
            </span>
         </div>

        {/* ========== booking form ========== */}
         <div className="booking__form">
            <h5>Information</h5>
            <Form className="booking__info-form" onSubmit={handleClick}>
                <FormGroup>
                    <input type="text" placeholder="Full Name" id="fullName"
                    required onChange={handleChange} />
                </FormGroup>
                <FormGroup>
                    <input type="number" placeholder="Phone" id="phone"
                    required onChange={handleChange} />
                </FormGroup>
                <FormGroup className="d-flex align-items-center gap-3">
                    <input 
                      type="date" 
                      placeholder="" 
                      id="bookAt"
                      required 
                      onChange={handleChange} 
                    />
                    <input 
                      type="number" 
                      placeholder="Guest" 
                      id="guestSize"
                      required
                      onChange={handleChange} 
                    />
                </FormGroup>
            </Form>
         </div>
        {/* ========== booking end ========== */}

        {/* ========== booking bottom ========== */}
        <div className="booking__bottom">
            <ListGroup>
                <ListGroupItem className="border-0 px-0">
                    <h5 className="d-flex align-items-center gap-1">
                       ₹{price} <i class="ri-close-line"></i> 1 person
                    </h5>
                    <span> ₹{price}</span>
                </ListGroupItem>
                <ListGroupItem className="border-0 px-0">
                    <h5>Service charge</h5>
                    <span> ₹{serviceFee}</span>
                </ListGroupItem>
                <ListGroupItem className="border-0 px-0 total">
                    <h5>Total</h5>
                    <span> ₹{totalAmount}</span>
                </ListGroupItem>
            </ListGroup>

            <Button className="btn primary__btn w-100 mt-4" onClick={()=> handlePayment(totalAmount)}>Book Now</Button>
        </div>
    </div>
}

export default Booking;
