import React from 'react';

const TourDet = ({tour}) => {
  return (
    <div className="tour__card">
      <div className="tour__card__image">
      </div>
      <div className="tour__card__content">
        <h5 className="tour__card__title">{tour.title}</h5>
        <p className="tour__card__desc">{tour.desc}</p>
        <div className="tour__card__footer">
          <span className="tour__card__price">Price: ${tour.price}</span>
          <button onClick={handleDelete(tour._id)}>Delete</button>
        </div>
      </div>
    </div>
  );
};
const handleDelete = async (tourId) => {
  try {
    const response = await fetch(`${BASE_URL}/tours/${tourId}`, {
      method: 'DELETE'
    });
    if (response.ok) {
      // Reload the tours
      window.location.reload();
    } else {
      throw new Error('Failed to delete tour');
    }
  } catch (error) {
    console.error(error);
  }
};

export default TourDet;
